INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("12240244-f641-4041-97d0-71293b816212", "Stranger Things", "Em uma pequena cidade dos anos 80, um grupo de crianças se une para desvendar os mistérios que envolvem o desaparecimento de um de seus amigos e a aparição de uma garota com poderes telecinéticos. Conforme eles exploram o mundo invertido, segredos sombrios e ameaças sobrenaturais são revelados.", "https://api.deepai.org/job-view-file/dfffe288-8143-419c-b7e8-a8d38bcea930/outputs/output.jpg", "67b9e2b6-4991-447b-a8cf-d9e07dfee917");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("7bb71454-c99e-4959-99e8-2c6b610b5f48", "Black Mirror", "Cada episódio de 'Black Mirror' conta uma história independente, explorando a relação entre a tecnologia e a sociedade de maneira sombria e distópica. Com temáticas como redes sociais, realidade virtual e inteligência artificial, a série nos faz refletir sobre as consequências do avanço tecnológico.", "https://i.pinimg.com/564x/ab/1e/e6/ab1ee6c0880595437fc2d5c4f25c1273.jpg", "67b9e2b6-4991-447b-a8cf-d9e07dfee917");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("63667b5e-4247-4fc1-a0db-947d5ad25026", "Money Heist (La Casa de Papel)", "Um grupo de criminosos liderado pelo Professor realiza o maior assalto da história à Casa da Moeda da Espanha. Enquanto eles tentam imprimir bilhões de euros e lidar com os reféns, a série apresenta reviravoltas, tensão e um jogo de gato e rato com a polícia.", "https://api.deepai.org/job-view-file/f74ab4c6-18ae-4e16-a83e-2c7c69957af7/outputs/output.jpg", "67b9e2b6-4991-447b-a8cf-d9e07dfee917");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("6fc4080e-f29b-48f7-8bc5-c48692a02bac", "The Crown", "'The Crown' é uma série dramática que segue a vida da Rainha Elizabeth II desde o início de seu reinado até os eventos mais recentes da família real britânica. Com atuações brilhantes, a série explora o poder, as intrigas políticas e os desafios pessoais enfrentados pela monarca.", "https://api.deepai.org/job-view-file/920b9dde-de06-4a23-9d53-f34c6aa531da/outputs/output.jpg", "67b9e2b6-4991-447b-a8cf-d9e07dfee917");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("09426421-a33c-457d-a611-9fd04e555725", "Narcos", "Inspirada em eventos reais, 'Narcos' narra a ascensão e a queda do império do tráfico de drogas liderado por Pablo Escobar, o mais famoso narcotraficante colombiano. A série aborda a violência, a corrupção e as complexidades do mundo do crime organizado.", "https://api.deepai.org/job-view-file/73a5a8bc-13d3-4bc2-8cb4-b98968838539/outputs/output.jpg", "67b9e2b6-4991-447b-a8cf-d9e07dfee917");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("72fc5775-0869-4dba-9a70-da4e20f5ddcd", "Breaking Bad", "'Breaking Bad' acompanha a jornada de Walter White, um professor de química diagnosticado com câncer que se transforma em um produtor de metanfetamina para garantir o futuro financeiro de sua família. A série explora temas como moralidade, consequências e transformação pessoal.", "https://api.deepai.org/job-view-file/da607804-aad6-43e1-a7a6-fd2382a89222/outputs/output.jpg", "67b9e2b6-4991-447b-a8cf-d9e07dfee917");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("383731a5-884b-4aa2-bcf2-dd42fe10b0e2", "The Marvelous Mrs. Maisel", "Ambientada na década de 1950, a série segue a vida de Miriam 'Midge' Maisel, uma dona de casa judia que descobre seu talento para a comédia stand-up. Enfrentando os desafios do mundo masculino do showbiz, ela se torna uma das comediantes mais populares de Nova York.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "1add9014-2a79-45b6-be9c-aaa587ea6a9d");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("09c80079-f5e0-4936-9068-ab48c7f08306", "The Boys", "Em um mundo onde super-heróis são celebridades corruptas, um grupo de vigilantes decide se opor a eles e expor a verdade por trás de sua fachada. Com muita ação, humor negro e crítica social, a série desconstrói o conceito tradicional de super-heróis.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "1add9014-2a79-45b6-be9c-aaa587ea6a9d");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("51c12e20-3bb2-4b0b-a1fe-96b4a0abe964", "Fleabag", "'Fleabag' é uma comédia dramática que segue a vida de uma mulher irreverente e sincera enquanto ela navega por relacionamentos, família disfuncional e traumas pessoais. Com um estilo único e quebra da quarta parede, a série aborda temas como amor, perda e identidade.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "1add9014-2a79-45b6-be9c-aaa587ea6a9d");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("09a2ea80-8765-4eea-a8f7-9b2bfa7efc4c", "The Expanse", "Situada no futuro, 'The Expanse' é uma série de ficção científica que acompanha a tensão política entre a Terra, Marte e o Cinturão de Asteroides. Com uma trama complexa, personagens cativantes e efeitos visuais impressionantes, a série explora temas como colonização espacial e descobertas alienígenas.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "1add9014-2a79-45b6-be9c-aaa587ea6a9d");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("ad968c5f-1bc7-4335-90e7-a63624f16ef1", "Hunters", "Na Nova York dos anos 70, um grupo de caçadores de nazistas secretos descobre uma conspiração para criar um Quarto Reich nos Estados Unidos. Com uma mistura de drama e violência, a série oferece uma narrativa intensa e cheia de reviravoltas.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "1add9014-2a79-45b6-be9c-aaa587ea6a9d");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("95387d7e-2b7e-497c-89f7-df2302d961dd", "Good Omens", "Baseada no livro de mesmo nome de Terry Pratchett e Neil Gaiman, 'Good Omens' segue um anjo e um demônio que se unem para impedir o apocalipse e salvar a humanidade. Com um humor peculiar e uma abordagem única do tema, a série combina fantasia, comédia e aventura.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "1add9014-2a79-45b6-be9c-aaa587ea6a9d");
