INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("50790cf9-a708-40c8-90fd-09da22c616b3", "WandaVision", "Após os eventos de 'Vingadores: Ultimato', Wanda Maximoff e Visão vivem uma vida suburbana aparentemente perfeita. No entanto, a realidade começa a se desfazer, revelando segredos e mistérios. Com uma homenagem às sitcoms clássicas e elementos de super-heróis, a série oferece uma experiência única.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "f6a91b07-41db-49e9-90d8-8a5f2d2700f6");
