INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("496f2db8-d7f0-4b31-8625-099fa1d457a2", "The Simpsons", "A série animada mais longa da história da televisão, 'The Simpsons' acompanha as divertidas e às vezes caóticas aventuras da família amarela de Springfield. Com um humor irreverente e satírico, a série aborda questões sociais, culturais e familiares.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "f6a91b07-41db-49e9-90d8-8a5f2d2700f6");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("8ee9f510-8954-4bf9-ad7b-4876cf6c1bd0", "Loki", "Após os eventos de 'Vingadores: Ultimato', Loki, o Deus da Trapaça, embarca em uma jornada através do tempo e do espaço, enfrentando desafios e alterando a história. Com um tom cômico e uma mistura de ação e fantasia, a série explora o carismático personagem de Loki.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "f6a91b07-41db-49e9-90d8-8a5f2d2700f6");
