INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("7a77897a-5247-4d14-969b-3cf50624ae1e", "The Handmaid's Tale", "Baseada no livro de Margaret Atwood, 'The Handmaid's Tale' se passa em uma sociedade distópica onde as mulheres são subjugadas e usadas como reprodutoras. A série segue a jornada de Offred, uma das 'aias', que luta pela liberdade e por um futuro melhor.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "3487372c-29ba-425f-96c4-d2c6f8895396");
