INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("e8b4955a-18bb-489e-ae39-3a27c17a2d1b", "Normal People", "Baseada no romance de mesmo nome, 'Normal People' segue a complexa história de amor entre Marianne e Connell, dois jovens irlandeses de origens sociais diferentes. Com uma abordagem sensível e realista, a série explora temas como relacionamentos, identidade e vulnerabilidade.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "3487372c-29ba-425f-96c4-d2c6f8895396");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("30fd2054-d396-45e6-8740-db1392fb2e31", "Harlots", "Situada na Londres do século XVIII, 'Harlots' narra a rivalidade entre duas casas de prostituição e as mulheres que lutam por poder, independência e sobrevivência. Com uma visão feminina e perspicaz, a série aborda questões de gênero, classe social e sexualidade.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "3487372c-29ba-425f-96c4-d2c6f8895396");
