INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("64bbffcc-11ec-442e-be2e-fbf40fca3ac0", "Game of Thrones", "'Game of Thrones' é uma série épica de fantasia baseada nos livros de George R.R. Martin. Situada em um mundo medieval fictício, a trama acompanha diversas famílias nobres que lutam pelo controle do Trono de Ferro e pelo domínio dos Sete Reinos. Com intriga política, batalhas sangrentas e reviravoltas chocantes, a série se tornou um fenômeno global.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "527d27a0-4395-47ac-bedf-b0c7fb4e998a");
INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("85355b3e-ffa1-4c84-b70f-ce6215fc9089", "Westworld", "'Westworld' se passa em um parque temático futurístico onde os visitantes podem interagir com androides chamados de 'anfitriões'. A série mergulha nas questões da consciência artificial, livre arbítrio e ética, enquanto os anfitriões começam a questionar sua própria existência. Com uma narrativa complexa e cheia de reviravoltas, 'Westworld' desafia as percepções sobre realidade e identidade.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "527d27a0-4395-47ac-bedf-b0c7fb4e998a");
