INSERT INTO series (id, name, description, image_url, platform_id) VALUES ("02f5ee41-d8aa-4bfa-8640-c96428656a2e", "Chernobyl", "'Chernobyl' é uma minissérie baseada no desastre nuclear de Chernobyl ocorrido em 1986. A trama retrata os eventos que levaram ao acidente, os esforços para controlar a crise e as consequências devastadoras. Com uma abordagem realista e comovente, a série explora os aspectos humanos, políticos e científicos dessa catástrofe histórica.", "https://api.deepai.org/job-view-file/3148cc57-e245-4032-b26e-01041cb3b854/outputs/output.jpg", "527d27a0-4395-47ac-bedf-b0c7fb4e998a");
